  <script>
    document.addEventListener('DOMContentLoaded', function() {
      // Create minute markers
      const minuteMarkers = document.querySelector('.minute-markers');
      for (let i = 0; i < 60; i++) {
        const marker = document.createElement('div');
        marker.className = 'minute-marker';
        marker.style.transform = `rotate(${i * 6}deg)`;
        minuteMarkers.appendChild(marker);
      }

      // Create floating particles
      const particlesContainer = document.getElementById('particles');
      for (let i = 0; i < 60; i++) {
        const particle = document.createElement('div');
        particle.className = 'particle';
        particle.style.left = `${Math.random() * 100}%`;
        particle.style.top = `${Math.random() * 100}%`;
        particle.style.animationDuration = `${15 + Math.random() * 30}s`;
        particle.style.animationDelay = `-${Math.random() * 15}s`;
        particlesContainer.appendChild(particle);
      }

      function updateClock() {
        const now = new Date();
        const seconds = now.getSeconds();
        const minutes = now.getMinutes();
        const hours = now.getHours() % 12;

        const secondDegrees = ((seconds / 60) * 360) + 90;
        const minuteDegrees = ((minutes / 60) * 360) + ((seconds / 60) * 6) + 90;
        const hourDegrees = ((hours / 12) * 360) + ((minutes / 60) * 30) + 90;

        document.querySelector('.second-hand').style.transform = `translateX(-50%) rotate(${secondDegrees}deg)`;
        document.querySelector('.minute-hand').style.transform = `translateX(-50%) rotate(${minuteDegrees}deg)`;
        document.querySelector('.hour-hand').style.transform = `translateX(-50%) rotate(${hourDegrees}deg)`;

        // Smooth transition for the second hand when it completes a full rotation
        if (seconds === 59) {
          document.querySelector('.second-hand').style.transition = 'transform 0.5s cubic-bezier(0.4, 2.3, 0.3, 1)';
        } else if (seconds === 0) {
          document.querySelector('.second-hand').style.transition = 'none';
          setTimeout(() => {
            document.querySelector('.second-hand').style.transition = 'transform 0.5s cubic-bezier(0.4, 2.3, 0.3, 1)';
          }, 50);
        }
      }

      updateClock();
      setInterval(updateClock, 1000);

      // 3D tilt effect
      const clock = document.querySelector('.clock');
      document.addEventListener('mousemove', (e) => {
        const rect = clock.getBoundingClientRect();
        const x = e.clientX - rect.left - rect.width / 2;
        const y = e.clientY - rect.top - rect.height / 2;

        clock.style.transform = `perspective(1000px) rotateX(${y * 0.03}deg) rotateY(${-x * 0.03}deg)`;
      });

      document.addEventListener('mouseleave', () => {
        clock.style.transform = 'perspective(1000px) rotateX(0) rotateY(0)';
      });
    });
  </script>
</body>
</html>
