      height: 100vh;
      margin: 0;
      background: radial-gradient(circle at center, #1a2a6c, #0a0e23);
      font-family: 'Cinzel', serif;
      overflow: hidden;
    }

    .clock-container {
      position: relative;
      filter: drop-shadow(var(--shadow-depth));
      perspective: 1000px;
    }

    .clock {
      width: 450px;
      height: 450px;
      border-radius: 50%;
      position: relative;
      background: var(--clock-bg);
      box-shadow: 
        var(--inner-glow),
        inset 0 0 40px rgba(0, 0, 0, 0.15),
        0 0 0 15px var(--gold-primary);
      transform-style: preserve-3d;
      transition: transform 0.8s cubic-bezier(0.25, 0.1, 0.25, 1);
    }

    .clock::before {
      content: '';
      position: absolute;
      width: 96%;
      height: 96%;
      border-radius: 50%;
      top: 2%;
      left: 2%;
      border: 1px solid rgba(255, 255, 255, 0.5);
      z-index: 1;
    }

    .clock-face {
      width: 100%;
      height: 100%;
      position: relative;
    }

    /* Hands */
    .hand {
      position: absolute;
      bottom: 50%;
      left: 50%;
      transform-origin: 50% 100%;
      z-index: 10;
      transition: transform 0.6s cubic-bezier(0.68, -0.55, 0.27, 1.55);
      filter: drop-shadow(0 0 3px rgba(0, 0, 0, 0.3));
    }

    .hour-hand {
      width: 10px;
      height: 110px;
      background: linear-gradient(to top, #333, #555);
      margin-left: -5px;
      border-radius: 5px 5px 0 0;
      transform: translateX(-50%) rotate(0deg);
    }

    .minute-hand {
      width: 6px;
      height: 170px;
      background: linear-gradient(to top, #444, #666);
      margin-left: -3px;
      border-radius: 3px 3px 0 0;
      transform: translateX(-50%) rotate(0deg);
    }

    .second-hand {
      width: 2px;
      height: 190px;
      background: linear-gradient(to top, #e74c3c, #c0392b);
      margin-left: -1px;
      transform: translateX(-50%) rotate(0deg);
      z-index: 12;
    }

    .central-ring {
      position: absolute;
      top: 50%;
      left: 50%;
      width: 24px;
      height: 24px;
      border: 3px solid var(--gold-primary);
      border-radius: 50%;
      transform: translate(-50%, -50%);
      z-index: 11;
      background: white;
      box-shadow: 0 0 10px rgba(212, 175, 55, 0.8);
    }

    .center-dot {
      position: absolute;
      top: 50%;
      left: 50%;
      width: 10px;
      height: 10px;
      background: #e74c3c;
      border-radius: 50%;
      transform: translate(-50%, -50%);
      z-index: 13;
      box-shadow: 0 0 5px #e74c3c;
    }

    /* Roman Numerals */
    .number {
      position: absolute;
      font-family: 'Cinzel', serif;
      font-size: 28px;
      font-weight: 700;
      color: #333;
      width: 40px;
      height: 40px;
      display: flex;
      justify-content: center;
      align-items: center;
      border-radius: 50%;
      transform: translate(-50%, -50%);
      z-index: 5;
      text-shadow: 0 2px 3px rgba(255, 255, 255, 0.8);
      transition: all 0.4s ease;
    }

    .number:hover {
      color: var(--gold-primary);
      transform: translate(-50%, -50%) scale(1.2);
      text-shadow: 0 0 8px var(--gold-secondary);
    }

    /* Mathematical Circular Positioning */
    .twelve { top: 6.5%; left: 50%; }
    .one { top: 14.5%; left: 78%; }
    .two { top: 29%; left: 91%; }
    .three { top: 50%; left: 96%; transform: translateY(-50%); }
    .four { bottom: 29%; left: 91%; }
    .five { bottom: 14.5%; left: 78%; }
    .six { bottom: 6.5%; left: 50%; }
    .seven { bottom: 14.5%; left: 22%; }
    .eight { bottom: 29%; left: 9%; }
    .nine { top: 50%; left: 4%; transform: translateY(-50%); }
    .ten { top: 29%; left: 9%; }
    .eleven { top: 14.5%; left: 22%; }

    /* Minute Markers */
    .minute-markers {
      position: absolute;
      width: 100%;
      height: 100%;
    }

    .marker {
      position: absolute;
      left: 50%;
      width: 1px;
      height: 8px;
      background: rgba(0, 0, 0, 0.25);
      transform-origin: 50% 225px;
    }

    /* Floating Particles */
    .particles {
      position: absolute;
      width: 100%;
      height: 100%;
      border-radius: 50%;
      overflow: hidden;
    }

    .particle {
      position: absolute;
      width: 2px;
      height: 2px;
      background: var(--gold-secondary);
      border-radius: 50%;
      opacity: 0;
      animation: float 12s linear infinite;
    }

    @keyframes float {
      0% { transform: translateY(0) rotate(0deg); opacity: 0; }
      10% { opacity: 0.7; }
      100% { transform: translateY(-500px) rotate(360deg); opacity: 0; }
    }

    /* Ambient Glow */
    .glow {
      position: absolute;
      width: 110%;
      height: 110%;
      top: -5%;
      left: -5%;
      border-radius: 50%;
      background: radial-gradient(circle, rgba(212, 175, 55, 0.15) 0%, rgba(0,0,0,0) 70%);
      opacity: 0;
      transition: opacity 0.5s ease;
    }

    .clock-container:hover .glow {
      opacity: 1;
    }

    /* Decorative Elements */
    .decoration {
      position: absolute;
      border: 1px solid rgba(212, 175, 55, 0.3);
      border-radius: 50%;
      animation: pulse 6s infinite alternate;
    }

    .decoration-1 {
      width: 120%;
      height: 120%;
      top: -10%;
      left: -10%;
      animation-delay: 0.5s;
    }

    .decoration-2 {
      width: 140%;
      height: 140%;
      top: -20%;
      left: -20%;
      animation-delay: 1s;
    }

    @keyframes pulse {
      0% { transform: scale(1); opacity: 0.3; }
      100% { transform: scale(1.05); opacity: 0.1; }
    }

    /* Chromatic Flare Effect */
    .flare {
      position: absolute;
      width: 50px;
      height: 150px;
      background: linear-gradient(to right, 
        transparent, 
        rgba(255, 255, 255, 0.6), 
        transparent);
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%) rotate(30deg);
      opacity: 0;
      transition: opacity 0.3s, transform 0.5s;
      mix-blend-mode: overlay;
    }

    .clock-container:hover .flare {
      opacity: 0.3;
      transform: translate(-50%, -50%) rotate(390deg);
      transition: transform 3s linear, opacity 0.3s;
    }
  </style>
</head>
<body>
  <div class="clock-container">
    <div class="decoration decoration-1"></div>
    <div class="decoration decoration-2"></div>
    <div class="glow"></div>

    <div class="clock">
      <div class="particles" id="particles"></div>
      <div class="flare"></div>

      <div class="clock-face">
        <div class="minute-markers" id="markers"></div>

        <div class="hand hour-hand"></div>
        <div class="hand minute-hand"></div>
        <div class="hand second-hand"></div>

        <div class="central-ring"></div>
        <div class="center-dot"></div>

        <!-- Roman Numerals -->
        <div class="number twelve">XII</div>
        <div class="number one">I</div>
        <div class="number two">II</div>
        <div class="number three">III</div>
        <div class="number four">IV</div>
        <div class="number five">V</div>
        <div class="number six">VI</div>
        <div class="number seven">VII</div>
        <div class="number eight">VIII</div>
        <div class="number nine">IX</div>
        <div class="number ten">X</div>
        <div class="number eleven">XI</div>
      </div>
    </div>
  </div>

  <script>
    document.addEventListener('DOMContentLoaded', function() {
      // Create minute markers
      const markers = document.getElementById('markers');
      for (let i = 0; i < 60; i++) {
        const marker = document.createElement('div');
        marker.className = 'marker';
        marker.style.transform = `rotate(${i * 6}deg)`;
        markers.appendChild(marker);
      }

      // Floating golden particles
      const particles = document.getElementById('particles');
      for (let i = 0; i < 80; i++) {
        const particle = document.createElement('div');
        particle.className = 'particle';
        particle.style.left = `${Math.random() * 100}%`;
        particle.style.top = `${70 + Math.random() * 30}%`;
        particle.style.animationDelay = `${Math.random() * 15}s`;
        particle.style.animationDuration = `${10 + Math.random() * 15}s`;
        particles.appendChild(particle);
      }

      // Clock movement
      function updateClock() {
        const now = new Date();
        const seconds = now.getSeconds();
        const minutes = now.getMinutes();
        const hours = now.getHours() % 12;

        const secondDegrees = ((seconds / 60) * 360) + 90;
        const minuteDegrees = ((minutes / 60) * 360) + ((seconds / 60) * 6) + 90;
        const hourDegrees = ((hours / 12) * 360) + ((minutes / 60) * 30) + 90;

        document.querySelector('.second-hand').style.transform = `translateX(-50%) rotate(${secondDegrees}deg)`;
        document.querySelector('.minute-hand').style.transform = `translateX(-50%) rotate(${minuteDegrees}deg)`;
        document.querySelector('.hour-hand').style.transform = `translateX(-50%) rotate(${hourDegrees}deg)`;

        if (seconds === 59) {
          document.querySelector('.second-hand').style.transition = 'transform 0.6s cubic-bezier(0.68, -0.55, 0.27, 1.55)';
        } else if (seconds === 0) {
          document.querySelector('.second-hand').style.transition = 'none';
          setTimeout(() => {
            document.querySelector('.second-hand').style.transition = 'transform 0.6s cubic-bezier(0.68, -0.55, 0.27, 1.55)';
          }, 50);
        }
      }

      updateClock();
      setInterval(updateClock, 1000);

      // 3D Tilt Effect
      const clock = document.querySelector('.clock');
      document.addEventListener('mousemove', (e) => {
        const rect = clock.getBoundingClientRect();
        const x = e.clientX - rect.left - rect.width / 2;
        const y = e.clientY - rect.top - rect.height / 2;

        clock.style.transform = `perspective(1000px) rotateX(${y * 0.02}deg) rotateY(${-x * 0.02}deg)`;
      });

      document.addEventListener('mouseleave', () => {
        clock.style.transform = 'perspective(1000px) rotateX(0) rotateY(0)';
      });
    });
  </script>
</body>
</html>
