    <script>
        // Game state
        let board = [];
        let rows = 10;
        let cols = 10;
        let minesCount = 10;
        let flagsCount = 0;
        let revealedCount = 0;
        let gameOver = false;
        let firstClick = true;
        let timer = 0;
        let timerInterval = null;
        let hintUsed = false;
        let darkMode = true;
        let soundEnabled = true;
        let zoomEnabled = false;
        let autoSolving = false;
        let tutorialActive = false;

        // DOM elements
        const boardElement = document.getElementById('board');
        const minesCountElement = document.getElementById('mines-count');
        const flagsCountElement = document.getElementById('flags-count');
        const timeElement = document.getElementById('time');
        const gameOverModal = document.getElementById('game-over-modal');
        const gameResultElement = document.getElementById('game-result');
        const gameStatsElement = document.getElementById('game-stats');
        const playAgainBtn = document.getElementById('play-again-btn');
        const newGameBtn = document.getElementById('new-game-btn');
        const hintBtn = document.getElementById('hint-btn');
        const themeToggleBtn = document.getElementById('theme-toggle');
        const customModal = document.getElementById('custom-modal');
        const zoomBtn = document.getElementById('zoom-btn');
        const soundToggleBtn = document.getElementById('sound-toggle');
        const solverToggleBtn = document.getElementById('solver-toggle');
        const tutorialHighlight = document.getElementById('tutorial-highlight');

        // Audio elements
        const clickSound = document.getElementById('click-sound');
        const flagSound = document.getElementById('flag-sound');
        const mineSound = document.getElementById('mine-sound');
        const winSound = document.getElementById('win-sound');

        // Difficulty buttons
        const easyBtn = document.getElementById('easy-btn');
        const mediumBtn = document.getElementById('medium-btn');
        const hardBtn = document.getElementById('hard-btn');
        const customBtn = document.getElementById('custom-btn');
        const startCustomBtn = document.getElementById('start-custom-btn');
        const cancelCustomBtn = document.getElementById('cancel-custom-btn');

        // Initialize game
        function initGame() {
            // Clear existing board
            boardElement.innerHTML = '';
            board = [];
            flagsCount = 0;
            revealedCount = 0;
            gameOver = false;
            firstClick = true;
            hintUsed = false;
            autoSolving = false;

            // Reset buttons
            hintBtn.disabled = false;
            hintBtn.style.opacity = '1';
            solverToggleBtn.textContent = 'ðŸ¤– Auto';

            // Stop timer if running
            if (timerInterval) {
                clearInterval(timerInterval);
                timerInterval = null;
            }
            timer = 0;
            updateTimeDisplay();

            // Update mines count display
            minesCountElement.textContent = minesCount;
            flagsCountElement.textContent = flagsCount;

            // Set CSS variables for grid size
            boardElement.style.setProperty('--rows', rows);
            boardElement.style.setProperty('--cols', cols);

            // Create board
            for (let r = 0; r < rows; r++) {
                board[r] = [];
                for (let c = 0; c < cols; c++) {
                    board[r][c] = {
                        isMine: false,
                        isRevealed: false,
                        isFlagged: false,
                        adjacentMines: 0
                    };

                    const cell = document.createElement('div');
                    cell.className = 'cell';
                    cell.dataset.row = r;
                    cell.dataset.col = c;

                    // Event listeners
                    cell.addEventListener('click', () => handleLeftClick(r, c));
                    cell.addEventListener('contextmenu', (e) => {
                        e.preventDefault();
                        handleRightClick(r, c);
                    });

                    boardElement.appendChild(cell);
                }
            }

            // Start tutorial if first time visiting
            if (localStorage.getItem('minesweeperTutorial') !== 'completed') {
                startTutorial();
            }
        }

        // Place mines after first click (to ensure first click is always safe)
        function placeMines(firstRow, firstCol) {
            let minesPlaced = 0;

            while (minesPlaced < minesCount) {
                const r = Math.floor(Math.random() * rows);
                const c = Math.floor(Math.random() * cols);

                // Don't place mine on first click or where a mine already exists
                if ((r !== firstRow || c !== firstCol) && !board[r][c].isMine) {
                    board[r][c].isMine = true;
                    minesPlaced++;
                }
            }

            // Calculate adjacent mines for each cell
            for (let r = 0; r < rows; r++) {
                for (let c = 0; c < cols; c++) {
                    if (!board[r][c].isMine) {
                        board[r][c].adjacentMines = countAdjacentMines(r, c);
                    }
                }
            }

            // Start timer after mines are placed
            startTimer();
        }

        // Count adjacent mines for a cell
        function countAdjacentMines(r, c) {
            let count = 0;

            // Check all 8 surrounding cells
            for (let dr = -1; dr <= 1; dr++) {
                for (let dc = -1; dc <= 1; dc++) {
                    if (dr === 0 && dc === 0) continue; // Skip current cell

                    const nr = r + dr;
                    const nc = c + dc;

                    // Check if neighbor is within bounds and is a mine
                    if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && board[nr][nc].isMine) {
                        count++;
                    }
                }
            }

            return count;
        }

        // Handle left click
        function handleLeftClick(r, c) {
            if (gameOver || board[r][c].isFlagged) return;

            // Play click sound
            if (soundEnabled) {
                clickSound.currentTime = 0;
                clickSound.play().catch(e => console.log("Audio play failed:", e));
            }

            // On first click, place mines (avoiding this cell)
            if (firstClick) {
                placeMines(r, c);
                firstClick = false;
            }

            // If clicked on a mine
            if (board[r][c].isMine) {
                revealMines(r, c);
                gameOver = true;
                if (soundEnabled) {
                    mineSound.currentTime = 0;
                    mineSound.play().catch(e => console.log("Audio play failed:", e));
                }
                endGame(false);
                return;
            }

            // Reveal cell if not already revealed
            if (!board[r][c].isRevealed) {
                revealCell(r, c);

                // Check for win condition
                checkWin();
            } else {
                // If clicking on a revealed number, try chord action
                chordCells(r, c);
            }
        }

        // Handle right click (flag)
        function handleRightClick(r, c) {
            if (gameOver || board[r][c].isRevealed) return;

            const cell = getCellElement(r, c);

            if (!board[r][c].isFlagged) {
                board[r][c].isFlagged = true;
                cell.textContent = 'ðŸš©';
                flagsCount++;
                cell.classList.add('flagged');

                // Play flag sound
                if (soundEnabled) {
                    flagSound.currentTime = 0;
                    flagSound.play().catch(e => console.log("Audio play failed:", e));
                }
            } else {
                board[r][c].isFlagged = false;
                cell.textContent = '';
                flagsCount--;
                cell.classList.remove('flagged');
            }

            flagsCountElement.textContent = flagsCount;
        }

        // Reveal a cell
        function revealCell(r, c) {
            if (board[r][c].isRevealed || board[r][c].isFlagged) return;

            const cell = getCellElement(r, c);
            board[r][c].isRevealed = true;
            cell.classList.add('revealed');
            revealedCount++;

            if (board[r][c].adjacentMines > 0) {
                cell.textContent = board[r][c].adjacentMines;
                cell.classList.add(`number-${board[r][c].adjacentMines}`);
            } else {
                // If no adjacent mines, reveal all adjacent cells recursively
                for (let dr = -1; dr <= 1; dr++) {
                    for (let dc = -1; dc <= 1; dc++) {
                        if (dr === 0 && dc === 0) continue; // Skip current cell

                        const nr = r + dr;
                        const nc = c + dc;

                        // Check if neighbor is within bounds
                        if (nr >= 0 && nr < rows && nc >= 0 && nc < cols) {
                            revealCell(nr, nc);
                        }
                    }
                }
            }
        }

        // Chord action (when clicking on a revealed number)
        function chordCells(r, c) {
            const cell = board[r][c];
            if (!cell.isRevealed || cell.adjacentMines === 0) return;

            // Count adjacent flags
            let adjacentFlags = 0;
            for (let dr = -1; dr <= 1; dr++) {
                for (let dc = -1; dc <= 1; dc++) {
                    if (dr === 0 && dc === 0) continue;

                    const nr = r + dr;
                    const nc = c + dc;

                    if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && board[nr][nc].isFlagged) {
                        adjacentFlags++;
                    }
                }
            }

            // If flags match adjacent mines, reveal all unflagged adjacent cells
            if (adjacentFlags === cell.adjacentMines) {
                for (let dr = -1; dr <= 1; dr++) {
                    for (let dc = -1; dc <= 1; dc++) {
                        if (dr === 0 && dc === 0) continue;

                        const nr = r + dr;
                        const nc = c + dc;

                        if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && 
                            !board[nr][nc].isRevealed && !board[nr][nc].isFlagged) {

                            if (board[nr][nc].isMine) {
                                revealMines(nr, nc);
                                gameOver = true;
                                if (soundEnabled) {
                                    mineSound.currentTime = 0;
                                    mineSound.play().catch(e => console.log("Audio play failed:", e));
                                }
                                endGame(false);
                                return;
                            }

                            revealCell(nr, nc);
                        }
                    }
                }

                // Check for win condition after chording
                checkWin();
            }
        }

        // Reveal all mines (when game is lost)
        function revealMines(clickedR, clickedC) {
            for (let r = 0; r < rows; r++) {
                for (let c = 0; c < cols; c++) {
                    if (board[r][c].isMine) {
                        const cell = getCellElement(r, c);
                        cell.textContent = 'ðŸ’£';

                        if (r === clickedR && c === clickedC) {
                            cell.classList.add('mine-revealed');
                            setTimeout(() => {
                                cell.style.animation = 'explode 0.5s forwards';
                            }, 100);
                        } else {
                            cell.classList.add(board[r][c].isFlagged ? 'flagged' : 'mine-revealed');
                        }
                    }
                }
            }
        }

        // Check if player has won
        function checkWin() {
            if (revealedCount === (rows * cols - minesCount)) {
                gameOver = true;
                if (soundEnabled) {
                    winSound.currentTime = 0;
                    winSound.play().catch(e => console.log("Audio play failed:", e));
                }
                endGame(true);
                createConfetti();
            }
        }

        // End game (win or lose)
        function endGame(isWin) {
            // Stop timer
            if (timerInterval) {
                clearInterval(timerInterval);
                timerInterval = null;
            }

            if (isWin) {
                // Flag all mines if won
                for (let r = 0; r < rows; r++) {
                    for (let c = 0; c < cols; c++) {
                        if (board[r][c].isMine && !board[r][c].isFlagged) {
                            board[r][c].isFlagged = true;
                            const cell = getCellElement(r, c);
                            cell.textContent = 'ðŸš©';
                            cell.classList.add('flagged');
                        }
                    }
                }

                // Disable hint button completely
                hintBtn.disabled = true;
                hintBtn.style.opacity = '0.5';
            }

            // Show game over modal
            gameResultElement.textContent = isWin ? 'You Won! ðŸŽ‰' : 'Game Over! ðŸ’¥';
            gameStatsElement.textContent = `Time: ${timer} seconds | Mines: ${minesCount} | Size: ${rows}x${cols}`;
            gameOverModal.style.display = 'flex';
        }

        // Start timer
        function startTimer() {
            timer = 0;
            updateTimeDisplay();
            timerInterval = setInterval(() => {
                timer++;
                updateTimeDisplay();

                // Add warning effect after 1 minute
                if (timer === 60) {
                    timeElement.classList.add('warning');
                }
            }, 1000);
        }

        // Update time display
        function updateTimeDisplay() {
            timeElement.textContent = timer;
        }

        // Get cell DOM element
        function getCellElement(r, c) {
            return document.querySelector(`.cell[data-row="${r}"][data-col="${c}"]`);
        }

        // Set difficulty
        function setDifficulty(difficulty) {
            switch (difficulty) {
                case 'easy':
                    rows = 10;
                    cols = 10;
                    minesCount = 10;
                    break;
                case 'medium':
                    rows = 16;
                    cols = 16;
                    minesCount = 40;
                    break;
                case 'hard':
                    rows = 16;
                    cols = 30;
                    minesCount = 99;
                    break;
                case 'custom':
                    // Custom settings handled separately
                    return;
            }

            // Update active button
            easyBtn.classList.remove('active');
            mediumBtn.classList.remove('active');
            hardBtn.classList.remove('active');
            customBtn.classList.remove('active');

            document.getElementById(`${difficulty}-btn`).classList.add('active');

            initGame();
        }

        // Show hint (reveal a safe cell)
        function showHint() {
            if (gameOver || firstClick || hintUsed) return;

            // Find all unrevealed, unflagged, non-mine cells
            const safeCells = [];
            for (let r = 0; r < rows; r++) {
                for (let c = 0; c < cols; c++) {
                    if (!board[r][c].isRevealed && !board[r][c].isFlagged && !board[r][c].isMine) {
                        safeCells.push({ r, c });
                    }
                }
            }

            if (safeCells.length > 0) {
                // Pick a random safe cell
                const { r, c } = safeCells[Math.floor(Math.random() * safeCells.length)];
                const cell = getCellElement(r, c);

                // Animation for hint
                cell.classList.add('highlight');

                setTimeout(() => {
                    cell.classList.remove('highlight');
                }, 1500);

                hintUsed = true;
                hintBtn.disabled = true;
                hintBtn.style.opacity = '0.5';
            }
        }

        // Create confetti effect for win
        function createConfetti() {
            const colors = ['#f00', '#0f0', '#00f', '#ff0', '#f0f', '#0ff'];
            const container = document.querySelector('.game-container');

            for (let i = 0; i < 100; i++) {
                const confetti = document.createElement('div');
                confetti.className = 'confetti';
                confetti.style.left = Math.random() * 100 + '%';
                confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
                confetti.style.animationDelay = Math.random() * 2 + 's';
                container.appendChild(confetti);

                // Remove after animation
                setTimeout(() => {
                    confetti.remove();
                }, 3000);
            }
        }

