            zoomEnabled = !zoomEnabled;
            boardElement.classList.toggle('zoomed', zoomEnabled);
            zoomBtn.textContent = zoomEnabled ? 'ðŸ” Zoom Out' : 'ðŸ” Zoom';
        }

        // Toggle sound
        function toggleSound() {
            soundEnabled = !soundEnabled;
            soundToggleBtn.textContent = soundEnabled ? 'ðŸ”” Sound' : 'ðŸ”• Mute';
        }

        // Toggle auto-solver
        function toggleSolver() {
            autoSolving = !autoSolving;
            solverToggleBtn.textContent = autoSolving ? 'ðŸ¤– Solving...' : 'ðŸ¤– Auto';

            if (autoSolving) {
                // Start auto solver
                autoSolveStep();
            }
        }

        // Auto-solver logic (basic implementation)
        function autoSolveStep() {
            if (!autoSolving || gameOver) return;

            // Simple logic: find obvious moves
            let madeMove = false;

            // Look for safe moves first
            for (let r = 0; r < rows; r++) {
                for (let c = 0; c < cols; c++) {
                    const cell = board[r][c];

                    // Only look at revealed cells with adjacent mines
                    if (cell.isRevealed && cell.adjacentMines > 0) {
                        // Count adjacent flagged and unrevealed cells
                        let adjacentFlags = 0;
                        let adjacentUnrevealed = 0;

                        for (let dr = -1; dr <= 1; dr++) {
                            for (let dc = -1; dc <= 1; dc++) {
                                if (dr === 0 && dc === 0) continue;

                                const nr = r + dr;
                                const nc = c + dc;

                                if (nr >= 0 && nr < rows && nc >= 0 && nc < cols) {
                                    if (board[nr][nc].isFlagged) {
                                        adjacentFlags++;
                                    } else if (!board[nr][nc].isRevealed) {
                                        adjacentUnrevealed++;
                                    }
                                }
                            }
                        }

                        // If adjacent flags equals the number, reveal unrevealed cells
                        if (cell.adjacentMines === adjacentFlags && adjacentUnrevealed > 0) {
                            for (let dr = -1; dr <= 1; dr++) {
                                for (let dc = -1; dc <= 1; dc++) {
                                    if (dr === 0 && dc === 0) continue;

                                    const nr = r + dr;
                                    const nc = c + dc;

                                    if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && 
                                        !board[nr][nc].isRevealed && !board[nr][nc].isFlagged) {

                                        // Reveal the cell
                                        handleLeftClick(nr, nc);
                                        madeMove = true;
                                        return setTimeout(autoSolveStep, 500);
                                    }
                                }
                            }
                        }

                        // If number - flags equals unrevealed, flag all unrevealed
                        if ((cell.adjacentMines - adjacentFlags) === adjacentUnrevealed && adjacentUnrevealed > 0) {
                            for (let dr = -1; dr <= 1; dr++) {
                                for (let dc = -1; dc <= 1; dc++) {
                                    if (dr === 0 && dc === 0) continue;

                                    const nr = r + dr;
                                    const nc = c + dc;

                                    if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && 
                                        !board[nr][nc].isRevealed && !board[nr][nc].isFlagged) {

                                        // Flag the cell
                                        handleRightClick(nr, nc);
                                        madeMove = true;
                                        return setTimeout(autoSolveStep, 500);
                                    }
                                }
                            }
                        }
                    }
                }
            }

            // If no safe moves found, make a random move
            if (!madeMove) {
                const unrevealedCells = [];
                for (let r = 0; r < rows; r++) {
                    for (let c = 0; c < cols; c++) {
                        if (!board[r][c].isRevealed && !board[r][c].isFlagged) {
                            unrevealedCells.push({ r, c });
                        }
                    }
                }

                if (unrevealedCells.length > 0) {
                    const { r, c } = unrevealedCells[Math.floor(Math.random() * unrevealedCells.length)];
                    handleLeftClick(r, c);
                    setTimeout(autoSolveStep, 500);
                } else {
                    autoSolving = false;
                    solverToggleBtn.textContent = 'ðŸ¤– Auto';
                }
            }
        }

